import React from 'react'
import "./NewExpenseForm.scss";

export default function NewExpenseForm() {
  const [data, setData] = React.useState({enteredTitle: '', enteredAmount: '', enteredDate: ''})
  const titleHandler = (e) => {
    setData({
      ...data,
      enteredTitle: e.target.value
    })
  }
  const amountHandler = (e) => {
    setData({
      ...data,
      enteredAmount: e.target.value
    })
  }
  const dateHandler = (e) => {
    setData({
      ...data,
      enteredDate: e.target.value
    })
  }
  console.log(data)
  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control" >
          <label>Title</label>
          <input type="text" onInput={titleHandler} />
        </div>
        <div className="new-expense__control" >
          <label>Amount</label>
          <input type="number" min="0.01" step="0.01" onChange={amountHandler} />
        </div>
        <div className="new-expense__control" >
          <label>Date</label>
          <input type="date" min="" max="" onChange={dateHandler} />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  )
}
